const slider = document.querySelector('.product-slider');
const sliderButtons = document.querySelectorAll('.slider-button');

let scrollAmount = 0;
let sliderWidth = slider.offsetWidth;
let cardMargin = parseInt(window.getComputedStyle(document.querySelector('.product-card')).marginRight);

sliderButtons.forEach(button => {
  button.addEventListener('click', function() {
    if (this.classList.contains('left')) {
      scrollAmount -= (sliderWidth + cardMargin);
      if (scrollAmount < 0) {
        scrollAmount = 0;
      }
    } else if (this.classList.contains('right')) {
      scrollAmount += (sliderWidth + cardMargin);
      if (scrollAmount > slider.scrollWidth - sliderWidth) {
        scrollAmount = slider.scrollWidth - sliderWidth;
      }
    }
    slider.scrollTo({
      top: 0,
      left: scrollAmount,
      behavior: 'smooth'
    });
  });
});

window.addEventListener('resize', function() {
  sliderWidth = slider.offsetWidth;
});

// Mouse drag functionality
let isDragging = false;
let startX;
let scrollLeft;

slider.addEventListener('mousedown', (e) => {
  isDragging = true;
  startX = e.pageX - slider.offsetLeft;
  scrollLeft = slider.scrollLeft;
  slider.classList.add('active'); // Optional: add a class to style the slider when dragging
});

slider.addEventListener('mouseleave', () => {
  isDragging = false;
  slider.classList.remove('active');
});

slider.addEventListener('mouseup', () => {
  isDragging = false;
  slider.classList.remove('active');
});

slider.addEventListener('mousemove', (e) => {
  if (!isDragging) return;
  e.preventDefault();
  const x = e.pageX - slider.offsetLeft;
  const walk = (x - startX) * 2; // Multiply by 2 to increase the drag speed
  slider.scrollLeft = scrollLeft - walk;
});
  
