    function gstValid() {
    var inputvalues = $("#gstno").val();    
    var stringLength = inputvalues.length;
    var gstinformat = new RegExp('^[0-9]{2}[A-Z]{5}[0-9]{4}');   

    if(stringLength === 15) {    
        if (gstinformat.test(inputvalues)) {    
            $("#gstno").prop("readonly", true);
            return true;    
        } else {    
            alert('Please Enter a Valid GSTIN Number');    
        } 
    } else {
        alert('Please Enter a Valid GSTIN Number (15 characters)');
    }     
