var debounceTimer;
function debounce(func, delay) {
    clearTimeout(debounceTimer);
    debounceTimer = setTimeout(func, delay);
}

$('.autocomplete_customer').off('input').on('input', function() {
    var searchTerm = $(this).val().trim();

    // Use debounce to limit the frequency of triggering completeCustomer function
    if(searchTerm.length >= 3){
    debounce(function() {
        // Call completeCustomer function with a delay
        completeCustomer(searchTerm);
    }, 300); // Delay in milliseconds
  }
});

$scope.completeCustomer = function () {
  var timeoutId;
var searchTerm;

$('.autocomplete_customer').off('input').on('input', function() {
    searchTerm = $(this).val().trim();
    console.log("Length of searchTerm:", searchTerm.length);
    if (searchTerm.length >= 3) {
        if (timeoutId) {
            clearTimeout(timeoutId);
        }

        timeoutId = setTimeout(function() {
            fetchAutocompleteData(searchTerm)
                .then(function(response) {
                    $scope.availableTagscus = response.data.list;
                    initializeAutocomplete($scope.availableTagscus);
                    $(".autocomplete_customer").autocomplete('search', searchTerm);
                })
                .catch(function(error) {
                    // Handle error cases if needed
                    console.error(error);
                });
        }, 200); // Set the delay time (in milliseconds)
    }else{
            fetchAutocompleteData('')
                .then(function(response) {
                    $scope.availableTagscus = response.data.list;
                    initializeAutocomplete($scope.availableTagscus);
                    $(".autocomplete_customer").autocomplete('search', searchTerm);
                })
                .catch(function(error) {
                    // Handle error cases if needed
                    console.error(error);
                });
    }
});

    function fetchAutocompleteData(searchTerm) {
        return $http({
            method: "POST",
            url: "<?php echo base_url() ?>index.php/customer/customer_search_full_by_single",
            data: {
                'action': 'fetch_single_data',
                'search': searchTerm,
                'order_no': '<?php echo $order_no; ?>',
                'tablename_sub': '<?php echo $tablename_sub; ?>'
            }
        });
    }

    function initializeAutocomplete(data) {
        var notallowedValues = [" starts with", " Contains with" ," with special charecters"]; // Values not allowed to be selected

        $(".autocomplete_customer").autocomplete({
            source: function(request, response) {
                response(data);
            },
           
            select: function(event, ui) {
                var selectedValue = ui.item.value;
                for (var i = 0; i < notallowedValues.length; i++) {
                    if (selectedValue.indexOf(notallowedValues[i]) !== -1) {
                      // setTimeout(closeAutocomplete, 100);
                        return false;
                    }
                }
                // setTimeout(closeAutocomplete, 100);
            },
            focus: function(event, ui) {
                var selectedValue = ui.item.value;
                for (var i = 0; i < notallowedValues.length; i++) {
                    if (selectedValue.indexOf(notallowedValues[i]) !== -1) {
                        return false;
                    }
                }   
            },
            open: function(event, ui) {
                $(".ui-menu-item").each(function(index) {
                  var itemClass = data[index].class;
                  $(this).addClass(itemClass);
                  var label = $(this).text();
                  var itemClass1 = getItemClassBasedOnLabel(label);
                    $(this).addClass(itemClass1);
                });
            },
            
        });
    }

    function getItemClassBasedOnLabel(label) {
        if (label.includes(" - starts with")) {
            return "cate_header";
        } else if (label.includes(" - Contains with")) {
            return "cate_header";
        }
        else if (label.includes(" - with special charecters")) {
            return "cate_header";
        }
        return "";
    }
};

        
